import React, { useState } from 'react';
import './App.css';

function App() {
    const [input, setInput] = useState('');
    const [response, setResponse] = useState(null);
    const [selectedFilters, setSelectedFilters] = useState([]);

    const handleSubmit = async () => {
        try {
            const res = await fetch('http://localhost:3000/bfhl', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ data: JSON.parse(input) }), // Make sure the input is properly formatted
            });
            if (!res.ok) {
                throw new Error(`Server error: ${res.statusText}`);
            }
            const data = await res.json();
            console.log('Response data:', data); // Debugging line
            setResponse(data);
        } catch (error) {
            console.error('Error:', error);
            alert('An error occurred: ' + error.message);
        }
    };

    const renderFilteredResponse = () => {
        if (!response) return null;
        const { numbers, alphabets, highest_lowercase_alphabet } = response;
        let result = [];
        if (selectedFilters.includes('Numbers')) result.push(`Numbers: ${numbers.join(',')}`);
        if (selectedFilters.includes('Alphabets')) result.push(`Alphabets: ${alphabets.join(',')}`);
        if (selectedFilters.includes('Highest lowercase alphabet')) result.push(`Highest Lowercase Alphabet: ${highest_lowercase_alphabet.join(',')}`);
        return result.join(' | ');
    };

    return (
        <div className="App">
            <h1>BFHL Project</h1>
            <input
                type="text"
                value={input}
                onChange={e => setInput(e.target.value)}
                placeholder="Enter JSON"
            />
            <button onClick={handleSubmit}>Submit</button>
            {response && (
                <div>
                    <label>Multi Filter</label>
                    <select
                        multiple
                        onChange={e => setSelectedFilters([...e.target.selectedOptions].map(option => option.value))}
                    >
                        <option value="Numbers">Numbers</option>
                        <option value="Alphabets">Alphabets</option>
                        <option value="Highest lowercase alphabet">Highest Lowercase Alphabet</option>
                    </select>
                    <div>Filtered Response: {renderFilteredResponse()}</div>
                </div>
            )}
        </div>
    );
}

export default App;
